#pragma config(Motor,  port4,           RWheel,       tmotorVex393_HBridge, openLoop)
#pragma config(Motor,  port3,           LWheel,       tmotorVex393_HBridge, openLoop)
#pragma config(Motor,  port6,           RClaw,       tmotorVex393_HBridge, openLoop)
#pragma config(Motor,  port5,           LClaw,       tmotorVex393_HBridge, openLoop)

//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

task main()
{
	while (1 == 1)
	{
	motor[RWheel] = vexRT(Ch2)*-.98;
	motor[LWheel] = vexRT(Ch3)*.98;

	if(vexRT[Btn6U] == 1) //WHEN BUTTON 5U IS PUSHED, THEN THE LEFT CLAW MOTOR WILL BE SET TO 20
	{
		motor[LClaw] = 50;
		motor[RClaw] = 50;
	}

	else if(vexRT[Btn6D] == 1) //WHEN BUTTON 5D IS PUSHED, THEN THE LEFT CLAW MOTOR WILL BE SET TO -20
	{
		motor[LClaw] = -50;
		motor[LClaw] = -50;
	}
	else
		{
		motor[LClaw] = 0;
		motor[LClaw] = 0;	
		
	}

	}
}
